    // Update href of any anchor elements on the page (at the time this function runs).
    // This will update urls with "?promotion=freewebind" to "?promotion=freewebuk" if we can detect a UK ip address.
    function geoTargetFreewebPromotion(country) {
        // Function settings...
        const selector = "a[href]";
        const defaultPromotion = "freewebind";
        const data = {
            "United Kingdom": "freewebuk",
            // "India": ,
            // "United States": ,
        }

        // Function logic...
        const countrySpecificPromotion = data[country];
        const promotion = countrySpecificPromotion !== undefined ? countrySpecificPromotion : defaultPromotion

        document.querySelectorAll(selector).forEach(function (el) {
            const href = el.getAttribute('href');
            if (!href) {
                return;
            }
            const url = new URL(href, window.location);
            const existingPromotion = url.searchParams.get("promotion");
            if (existingPromotion && existingPromotion === defaultPromotion) {
                url.searchParams.set('promotion', promotion);
                el.href = url.href;
            }
        })
    }
