<script type="application/javascript">
// For these pages: https://www.builder.ai/studio-store-product/...
// eg geoTargetPrices("United Kingdom") is called by code in global site settings;

function geoTargetStudioStorePricing(country) {
    var savingsSelector = ".savings";
    var plansSelector = ".plans";
    var replacements = [
        // studio store pricing
        ["$395", "£295", "₹9,999"],
        ["$8,690", "£6,490", "₹219,978"],
        ["$9,480", "£7,080", "₹239,976"],

        // savings benchmark
        ["$168", "£123", "₹1,385"],
        ["$730", "£535", "₹6,001"],
        ["$8,758", "£6,420", "₹72,012"],
        ["$40", "£30", "₹500"],
    ];

    var countryPositions = {
        "United States": 0,
        "United Kingdom": 1,
        "India": 2,
    }

    var countryPosition = countryPositions[country];
    if (!countryPosition) {
        return 
    }

    function replace(el) {
        if (!el) return;
        for (var i = 0; i < replacements.length; i++) {
            var patterns = replacements[i];
            replaceInText(el, patterns, patterns[countryPosition]);
        }
    }

    var plans = document.querySelector(plansSelector);
    replace(plans);

    var savings = document.querySelector(savingsSelector);
    if (savings) savings = savings.parentElement;
    replace(savings);
}

function replaceInText(element, patterns, replacement) {
    var childNodes = element.childNodes;
    // for (const node of element.childNodes) {
    for (var i = 0; i < childNodes.length; i++) {
        var node = childNodes[i];
        switch (node.nodeType) {
            case Node.ELEMENT_NODE:
                replaceInText(node, patterns, replacement);
                break;
            case Node.TEXT_NODE:
                // for (const pattern of patterns) {
                for (var i = 0; i < patterns.length; i++) {
                    var pattern = patterns[i];
                    if (pattern != replacement) {
                        node.textContent = node.textContent.replace(pattern, replacement);
                    }
                }
                break;
            case Node.DOCUMENT_NODE:
                replaceInText(node, patterns, replacement);
                break;
             default:
                // ignore
        }
    }
}
