<script type="application/javascript">
  function setFieldFromCookie(selector, key) {
    const cookieValue = getCookie(key);
    if (cookieValue != null && cookieValue != "") {
      document.querySelectorAll(selector).forEach(function (el) {
        if (
          el.getAttribute("value") == "" ||
          el.getAttribute("value") != decodeURI(cookieValue)
        ) {
          el.setAttribute("value", decodeURI(cookieValue));
        }
      });
    }
  }

  function setFieldFromValue(selector, value) {
    if (!value) return;
    document.querySelectorAll(selector).forEach(function (el) {
      if (el.getAttribute("value") == "" || el.getAttribute("value") != value) {
        el.setAttribute("value", value);
      }
    });
  }

  // Set utm parameters in forms
  function setUTMParameters() {
    setFieldFromCookie("input[name='field[119]']", "utmCampaign");
    setFieldFromCookie("input[name='field[117]']", "utmSource");
    setFieldFromCookie("input[name='field[121]']", "utmMedium");
    setFieldFromCookie("input[name='field[144]']", "utmTerm");
    // we (by mistake) use two different fields (127 and 225) for utm_content:
    setFieldFromCookie("input[name='field[127]']", "utmContent");
    setFieldFromCookie("input[name='field[225]']", "utmContent");
    setFieldFromCookie("input[name='field[317]']", "gclid");
  }
  
  // Set segment and ga user id in forms
  function setAnalyticsUserIds() {
    try {
        var segment_user_anonymous_id = analytics.user().anonymousId();
        setFieldFromValue("input[name='field[385]']", segment_user_anonymous_id);
    } catch (err) {}
    try {
        var ga_client_id = window.ga.getAll()[0].get('clientId');
        setFieldFromValue("input[name='field[386]']", ga_client_id);
    } catch (err) {}
  }

  function setFormHiddenFields() {
    setUTMParameters();
    setAnalyticsUserIds();
  }
  
  function setFormHiddenFieldsRepeatedly() {
    setFormHiddenFields();
    
    // and try again after a delay (3 times)
    setTimeout(setFormHiddenFields, 5000);
    setTimeout(setFormHiddenFields, 10000);
    setTimeout(setFormHiddenFields, 15000);
  }

  function listener() {
    // set utm parameters in cookie
    if (getParam("utm_campaign") != null) {
      document.cookie = "utmCampaign=" + getParam("utm_campaign") + ";";
    }
    if (getParam("utm_medium") != null) {
      document.cookie = "utmMedium=" + getParam("utm_medium") + ";";
    }
    if (getParam("utm_source") != null) {
      document.cookie = "utmSource=" + getParam("utm_source") + ";";
    }
    if (getParam("utm_term") != null) {
      document.cookie = "utmTerm=" + getParam("utm_term") + ";";
    }
    if (getParam("utm_content") != null) {
      document.cookie = "utmContent=" + getParam("utm_content") + ";";
    }
    if (getParam("gclid") != null) {
      document.cookie = "gclid=" + getParam("gclid") + ";";
    }

    setFormHiddenFieldsRepeatedly()
  }
  
  // Added event listener on dom loading for storing and setting the UTM parameters and analytics user id hidden fields
  document.addEventListener("DOMContentLoaded", listener);
  // Add to load event too in case forms are added to the DOM after DOMContentLoaded
  document.addEventListener("load", setFormHiddenFieldsRepeatedly);
</script>
